package com_PerScholas_firstexample;
import java.lang.Math; 

public class ExcInt2Binary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Java program to demonstrate converting integer to Binary
		// java.lang.Integer.toBinaryString() method 
		
		
		
		        int l = 1; 
		        // returns the string representation of the unsigned int value 
		        // represented by the argument in binary (base 2) 
		        System.out.println("Binary of 1 is : "  + Integer.toBinaryString(l)); 
		  
		        l = 8; 
		        System.out.println("Binary of 8 is : " + Integer.toBinaryString(l)); 
		        
		        l = 33; 
		        System.out.println("Binary of 33 is :  " + Integer.toBinaryString(l)); 
		        
		        l = 78; 
		        System.out.println("Binary of 78 is : " + Integer.toBinaryString(l)); 
		        
		        l = 787; 
		        System.out.println("Binary of 787 is: " + Integer.toBinaryString(l));
		        
		        l = 33987; 
		        System.out.println("Binary of 33987 is: " + Integer.toBinaryString(l)); 
		        
		       
		    } 

	 
	
	}


